import React, { useState } from 'react';

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
    props.showalert("converted to uppercase", "success");
  };
  const handleloClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
    props.showalert("converted to lowerCase", "success");
  };

  const handleOnChange = (event) => {
    setText(event.target.value);
  
  };

  const handleClear = () => {
    setText('');
    props.showalert("Text all clear", "success");

  };

  const handleExtraSpaces = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(' '));
    props.showalert("Removed extra spaces", "success");
    document.title='Extra Spaces removed';

  };

  const textTRIM =()=>{
    let words=text.trim();
    let word_Length;
    if (words.length===0) {
      word_Length=0;
    }
    else{
      word_Length=words.split("").length;
    }
    props.showalert("trimming spaces", "success");
    return word_Length;
  }

  const [text, setText] = useState('Enter text here');

  return (
    <>
      <div className="container" style={{ color: props.mode === 'dark' ? 'white' : 'black' }}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea className="form-control" id="myBox" onChange={handleOnChange} style={{ backgroundColor: props.mode === 'dark' ? '#333' : 'white', color: props.mode === 'dark' ? 'white' : 'black' }} value={text} rows="8"></textarea>
        </div>
        <button className="btn btn-primary mx-1" onClick={handleUpClick}>Convert to Uppercase</button>
        <button className="btn btn-primary mx-1" onClick={handleClear}>Clear</button>
        <button className="btn btn-primary mx-1" onClick={handleloClick}>Convert to Lowercase</button>
        <button className="btn btn-primary mx-1" onClick={handleExtraSpaces}>Remove Extra Spaces</button>
        <button className="btn btn-primary mx-1" onClick={textTRIM}>Trimming Text</button>
      </div>
      <div className="container my-3" style={{ color: props.mode === 'dark' ? 'white' : 'black' }}>
        <h1>Your text summary</h1>
        <p>{text.split(" ").filter((element) => { return element !== "" }).length} words and {text.length} characters</p>
      </div>
    </>
  );
}
